import React, { useState } from 'react';

const CreateTemplate = () => {
    const [templateName, setTemplateName] = useState('');
    const [includeName, setIncludeName] = useState(false);

    const handleSubmit = (event) => {
        event.preventDefault();
        const data = { templateName, include_attendee_name: includeName };

        fetch('http://127.0.0.1:8000/monitoring/attendance_templates/', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            Authorization: 'Token yourTokenHere'
        },
        body: JSON.stringify(data)
        })
        .then(response => response.json())
        .then(data => {
            console.log('Success:', data);
            // Redirect or give feedback
        })
        .catch((error) => {
            console.error('Error:', error);
        });
    };

    return (
        <form onSubmit={handleSubmit} className="p-4">
        <h1 className="text-2xl font-bold mb-4">Create New Template</h1>
        <div className="mb-4">
            <label htmlFor="templateName" className="block text-sm font-medium text-gray-700">Template Name</label>
            <input type="text" id="templateName" name="templateName" value={templateName} onChange={e => setTemplateName(e.target.value)} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500" required />
        </div>
        <div className="mb-4">
            <label htmlFor="includeName" className="flex items-center">
            <input type="checkbox" id="includeName" name="includeName" checked={includeName} onChange={e => setIncludeName(e.target.checked)} className="mr-2" />
            Include Attendee Name
            </label>
        </div>
        <button type="submit" className="bg-blue-500 text-white px-6 py-2 rounded shadow hover:bg-blue-600">Submit</button>
        </form>
    );
};

export default CreateTemplate;
