import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Topbar from '../../components/Topbar';
import ProjLeadSidebar from '../../components/ProjLeadSideBar';
import { FaArrowLeft } from 'react-icons/fa';
import axios from 'axios';

const ProjLeadDailyAttRec = () => {
    const navigate = useNavigate();
    const [templateName, setTemplateName] = useState("");
    const [includeAttendeeName, setIncludeAttendeeName] = useState(true);
    const [includeGender, setIncludeGender] = useState(true);
    const [includeCollege, setIncludeCollege] = useState(false);
    const [includeDepartment, setIncludeDepartment] = useState(false);
    const [includeYearSection, setIncludeYearSection] = useState(false);
    const [includeAgencyOffice, setIncludeAgencyOffice] = useState(false);
    const [includeContactNumber, setIncludeContactNumber] = useState(true);
    const [expirationDate, setExpirationDate] = useState("");
    const [errors, setErrors] = useState({});
    const [createdTemplates, setCreatedTemplates] = useState([]);

    const handleViewClick = (path) => {
        navigate(path);
    };

    const handleCreateTemplate = async () => {
        setErrors({}); // Clear previous errors
        if (!expirationDate) {
            setErrors(prev => ({ ...prev, expirationDate: "Expiration date is required." }));
            return;
        }

        try {
            const response = await axios.post(
                'http://127.0.0.1:8000/monitoring/attendance_templates/',
                {
                    project: 1,  // Example project ID, replace with actual logic
                    templateName,
                    include_attendee_name: includeAttendeeName,
                    include_gender: includeGender,
                    include_college: includeCollege,
                    include_department: includeDepartment,
                    include_year_section: includeYearSection,
                    include_agency_office: includeAgencyOffice,
                    include_contact_number: includeContactNumber,
                    expiration_date: expirationDate,
                },
                {
                    headers: {
                        'Authorization': 'Token 547dca520cf2940cd3cada1bf5208411a27d3ce5',
                    },
                }
            );

            const { id: templateId, sharable_link: link } = response.data;
            setCreatedTemplates(prevTemplates => [
                ...prevTemplates,
                {
                    templateName,
                    link,
                    dateCreated: new Date().toLocaleDateString(),
                    expirationDate,
                    id: templateId
                }
            ]);

            alert(`Attendance Template Created! Share this link: ${link}`);
        } catch (error) {
            console.error("Error creating attendance template", error.response.data);
            setErrors(error.response.data.errors || { general: "Failed to create attendance template." });
        }
    };

    const handleCopyLink = (link) => {
        navigator.clipboard.writeText(link).then(() => {
            alert("Link copied to clipboard!");
        }).catch((err) => {
            console.error("Error copying link: ", err);
            alert("Failed to copy link.");
        });
    };

    const submittedFiles = [
        { fileName: "Day 1 Attendance.pdf", submittedBy: "Proponent A", date: "2024-10-10" },
        { fileName: "Day 2 Attendance.pdf", submittedBy: "Proponent B", date: "2024-10-11" },
        { fileName: "Day 3 Attendance.pdf", submittedBy: "Proponent C", date: "2024-10-12" }
    ];

    return (
        <div className="bg-gray-200 min-h-screen flex">
            <div className="w-1/5 fixed h-full">
                <ProjLeadSidebar />
            </div>
            <div className="flex-1 ml-[20%]">
                <Topbar />
                <div className="flex flex-col mt-14 px-10">
                    <div className="flex items-center mb-5">
                        <button className="mr-2" onClick={() => handleViewClick('/projlead/proj/req')}>
                            <FaArrowLeft />
                        </button>
                        <h1 className="text-2xl font-semibold">Daily Attendance Record/List of Participants</h1>
                    </div>

                    {/* Project Details Section */}
                    <div className="bg-white shadow-lg rounded-lg p-6 mb-6">
                        <h2 className="text-xl font-semibold text-center mb-4">Project Details</h2>
                        <div className="grid grid-cols-2 gap-4">
                            <div>
                                <label className="block text-sm font-medium text-gray-700">Project Title</label>
                                <p className="bg-gray-100 rounded-lg p-3 mt-1">Tesda Vocational</p>
                            </div>
                            <div>
                                <label className="block text-sm font-medium text-gray-700">Project Leader</label>
                                <p className="bg-gray-100 rounded-lg p-3 mt-1">Tabasan, Wynoah Louis</p>
                            </div>
                        </div>
                    </div>

                    {/* Display Created Templates */}
                    <div className="bg-white shadow-lg rounded-lg p-6 mb-6">
                        <h2 className="text-xl font-semibold text-center mb-4">Generated Attendance Links</h2>
                        <table className="min-w-full table-auto border-collapse">
                            <thead>
                                <tr className="border-b">
                                    <th className="p-2 text-left">Template Name</th>
                                    <th className="p-2 text-left">Link</th>
                                    <th className="p-2 text-left">Date Created</th>
                                    <th className="p-2 text-left">Expiration Date</th>
                                </tr>
                            </thead>
                            <tbody>
                                {createdTemplates.map((template, index) => (
                                    <tr key={index} className="border-b">
                                        <td className="p-2">{template.templateName}</td>
                                        <td className="p-2">
                                            <button
                                                onClick={() => handleCopyLink(template.link)}
                                                className="text-blue-500"
                                            >
                                                Copy Link
                                            </button>
                                        </td>
                                        <td className="p-2">{template.dateCreated}</td>
                                        <td className="p-2">{template.expirationDate}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>

                    {/* Submitted Files Section */}
                    <div className="bg-white shadow-lg rounded-lg p-6 mb-6">
                        <h2 className="text-xl font-semibold text-center mb-4">Submitted Files</h2>
                        <table className="min-w-full table-auto border-collapse">
                            <thead>
                                <tr className="border-b">
                                    <th className="p-2 text-left">File Name</th>
                                    <th className="p-2 text-left">Submitted By</th>
                                    <th className="p-2 text-left">Date Submitted</th>
                                </tr>
                            </thead>
                            <tbody>
                                {submittedFiles.map((file, index) => (
                                    <tr key={index} className="border-b">
                                        <td className="p-2">{file.fileName}</td>
                                        <td className="p-2">{file.submittedBy}</td>
                                        <td className="p-2">{file.date}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProjLeadDailyAttRec;
