import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const TemplateList = () => {
  const [templates, setTemplates] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:8000/monitoring/attendance_templates/', {
      headers: {
        Authorization: 'Token yourTokenHere',
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        if (Array.isArray(data)) {
          setTemplates(data);
        } else {
          setError('Unexpected API response format');
        }
      })
      .catch(error => {
        console.error('Error:', error);
        setError(error.message);
      });
  }, []);

  if (error) {
    return <div className="p-4 text-red-500">Error: {error}</div>;
  }

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Attendance Templates</h1>
      <div className="space-y-3">
        {templates.length > 0 ? (
          templates.map(template => (
            <div key={template.id} className="border p-3 rounded shadow-sm">
              <p className="text-lg font-semibold">{template.templateName}</p>
              <Link to={`/template/${template.id}`} className="text-blue-500 hover:underline">View Details</Link>
            </div>
          ))
        ) : (
          <p className="text-gray-500">No templates available.</p>
        )}
      </div>
      <Link to="/create-template" className="mt-4 inline-block bg-blue-500 text-white px-6 py-2 rounded shadow hover:bg-blue-600">
        Create New Template
      </Link>
    </div>
  );
};

export default TemplateList;
