import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const TemplateDetails = () => {
    const { id } = useParams();
    const [template, setTemplate] = useState(null);

    useEffect(() => {
        fetch(`http://127.0.0.1:8000/monitoring/attendance_templates/${id}/`, {
        headers: {
            Authorization: 'Token yourTokenHere'
        }
        })
        .then(response => response.json())
        .then(data => setTemplate(data))
        .catch(error => console.error('Error:', error));
    }, [id]);

    if (!template) {
        return <p>Loading...</p>;
    }

    return (
        <div className="p-4">
        <h1 className="text-2xl font-bold mb-4">Template Details</h1>
        <p className="text-lg"><strong>Name:</strong> {template.templateName}</p>
        <p className="text-lg"><strong>Include Name:</strong> {template.include_attendee_name ? 'Yes' : 'No'}</p>
        {/* Add more fields as needed */}
        </div>
    );
};

export default TemplateDetails;
