import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Topbar from "../../components/Topbar";
import ProponentsSideBar from "../../components/ProponentsSideBar";
import { FaArrowLeft } from "react-icons/fa";

const ProponentsDailyAttRec = () => {
    const navigate = useNavigate();
    const { projectID } = useParams();
    const [projectDetails, setProjectDetails] = useState(null);
    const [date, setDate] = useState("");
    const [description, setDescription] = useState("");
    const [totalAttendees, setAttendees] = useState(0);
    const [attachedFiles, setAttachedFiles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [submissions, setSubmissions] = useState([]);
    const currentUser = localStorage.getItem("userFullName");

    useEffect(() => {
        if (!projectID) {
            console.error("Project ID is undefined.");
            return;
        }

        const fetchProjectData = async () => {
            const token = localStorage.getItem("authToken");
            if (!token) {
                alert("User not logged in. Please log in again.");
                navigate("/login");
                return;
            }

            try {
                const [projectRes, submissionsRes] = await Promise.all([
                    fetch(`http://127.0.0.1:8000/monitoring/projects/${projectID}/details/`, {
                        method: "GET",
                        headers: {
                            Authorization: `Token ${token}`,
                            "Content-Type": "application/json",
                        },
                    }),
                    fetch(
                        `http://127.0.0.1:8000/monitoring/project/${projectID}/checklist/Daily Attendance/submissions/`,
                        {
                            method: "GET",
                            headers: {
                                Authorization: `Token ${token}`,
                                "Content-Type": "application/json",
                            },
                        }
                    ),
                ]);

                if (projectRes.ok && submissionsRes.ok) {
                    const projectData = await projectRes.json();
                    const submissionsData = await submissionsRes.json();
                    setProjectDetails(projectData.projectDetails);
                    setSubmissions(submissionsData.submissions);
                } else {
                    console.error("Failed to fetch project data.");
                }
            } catch (error) {
                console.error("Error fetching project data:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchProjectData();
    }, [projectID, navigate]);

    const handleDelete = async (submissionId) => {
        const token = localStorage.getItem("authToken");
        if (!token) {
            alert("User not logged in or invalid session.");
            return;
        }

        if (!window.confirm("Are you sure you want to delete this submission?")) return;

        try {
            const response = await fetch(
                `http://127.0.0.1:8000/monitoring/submissions/${submissionId}/`,
                {
                    method: "DELETE",
                    headers: { Authorization: `Token ${token}` },
                }
            );

            if (response.ok) {
                alert("Submission deleted successfully!");
                setSubmissions((prev) =>
                    prev.filter((submission) => submission.submission_id !== submissionId)
                );
            } else {
                const errorData = await response.json();
                alert(`Error: ${errorData.error || "Failed to delete submission."}`);
            }
        } catch (error) {
            console.error("Error deleting submission:", error);
            alert("An error occurred. Please try again.");
        }
    };

    const handleFileChange = (event) => {
        const files = Array.from(event.target.files);
        setAttachedFiles((prevFiles) => [...prevFiles, ...files]);
    };

    const handleSubmit = async () => {
        const token = localStorage.getItem("authToken");
        if (!token) {
            alert("User not logged in or invalid session.");
            return;
        }

        if (attachedFiles.length === 0) {
            alert("Please attach at least one file.");
            return;
        }

        const formData = new FormData();
        formData.append("description", description);
        formData.append("total_attendees", totalAttendees);
        attachedFiles.forEach((file) => formData.append("attendance_file", file));

        try {
            const response = await fetch(
                `http://127.0.0.1:8000/monitoring/upload/attendance/${projectID}/`,
                {
                    method: "POST",
                    headers: { Authorization: `Token ${token}` },
                    body: formData,
                }
            );

            if (response.ok) {
                const data = await response.json();
                alert("Submission successful!");
                console.log("Submission successful:", data);
            } else {
                const errorData = await response.json();
                alert(`Error: ${errorData.error || "Submission failed!"}`);
            }
        } catch (error) {
            console.error("Error during submission:", error);
            alert("An error occurred. Please try again later.");
        }
    };

    if (loading) return <div>Loading project details...</div>;
    if (!projectDetails) return <div>Project not found.</div>;

    return (
        <div className="bg-gray-200 min-h-screen flex">
            <div className="w-1/5 fixed h-full">
                <ProponentsSideBar />
            </div>
            <div className="flex-1 ml-[20%]">
                <Topbar />
                <div className="flex flex-col mt-14 px-10">
                    <div className="flex items-center mb-5">
                        <button
                            className="mr-2"
                            onClick={() => navigate(`/proponents/proj/req/${projectID}`)}
                        >
                            <FaArrowLeft />
                        </button>
                        <h1 className="text-2xl font-semibold">Daily Attendance Record</h1>
                    </div>

                    <div className="bg-white shadow-lg rounded-lg p-6 mb-6">
                        <h2 className="text-xl font-semibold text-center mb-4">Project Details</h2>
                        <div className="grid grid-cols-3 gap-4">
                            {[
                                { label: "Project Title", value: projectDetails.projectTitle },
                                { label: "Project Leader", value: projectDetails.projectLeader },
                                { label: "College/Campus", value: projectDetails.college },
                                { label: "Target Date", value: projectDetails.targetDate },
                                { label: "Partner Agency", value: projectDetails.partnerAgency },
                            ].map(({ label, value }) => (
                                <div key={label}>
                                    <label className="block text-sm font-medium text-gray-700">
                                        {label}
                                    </label>
                                    <p className="bg-gray-100 rounded-lg p-3 mt-1">{value}</p>
                                </div>
                            ))}
                        </div>
                    </div>

                    <div className="bg-white shadow-lg rounded-lg p-8">
                        <h2 className="text-xl font-semibold text-center mb-6">
                            Add New Submission
                        </h2>
                        <div className="grid grid-cols-3 gap-4 mb-6">
                            {[
                                {
                                    label: "Description",
                                    type: "text",
                                    value: description,
                                    onChange: setDescription,
                                    placeholder: "Enter Description",
                                },
                                {
                                    label: "Date",
                                    type: "date",
                                    value: date,
                                    onChange: setDate,
                                },
                                {
                                    label: "Total Number of Attendees",
                                    type: "number",
                                    value: totalAttendees,
                                    onChange: setAttendees,
                                    placeholder: "Number of Attendees",
                                },
                            ].map(({ label, type, value, onChange, placeholder }) => (
                                <div key={label}>
                                    <label className="block text-sm font-medium text-gray-700">
                                        {label}
                                    </label>
                                    <input
                                        type={type}
                                        value={value}
                                        onChange={(e) => onChange(e.target.value)}
                                        placeholder={placeholder}
                                        className="bg-gray-100 rounded-lg p-3 mt-1 w-full"
                                    />
                                </div>
                            ))}
                        </div>

                        <div className="border border-gray-300 rounded-lg p-4 mb-6 relative">
                            <h3 className="font-semibold text-center mb-3">Attach Files</h3>
                            {attachedFiles.length === 0 && (
                                <div className="text-gray-400 mb-3">
                                    <span className="block text-center text-3xl">+</span>
                                </div>
                            )}
                            <input
                                type="file"
                                multiple
                                onChange={handleFileChange}
                                className="absolute inset-0 opacity-0 cursor-pointer"
                            />
                            {attachedFiles.length > 0 && (
                                <div
                                    className="grid grid-cols-5 gap-3 mt-4 w-full overflow-y-auto"
                                    style={{ maxHeight: "250px" }}
                                >
                                    {attachedFiles.map((file, index) => {
                                        const fileExtension = file.name.split(".").pop().toUpperCase();
                                        const preview = file.type.startsWith("image/") ? (
                                            <img
                                                src={URL.createObjectURL(file)}
                                                alt={`Preview-${index}`}
                                                className="h-20 w-20 object-cover rounded-lg"
                                            />
                                        ) : (
                                            <div className="h-20 w-20 bg-gray-200 flex items-center justify-center rounded-lg text-gray-600">
                                                {fileExtension}
                                            </div>
                                        );

                                        return (
                                            <div
                                                key={index}
                                                className="flex flex-col items-center border border-gray-200 rounded-lg p-2 shadow-md"
                                            >
                                                {preview}
                                                <p className="text-xs mt-2 text-center truncate w-full">
                                                    {file.name}
                                                </p>
                                            </div>
                                        );
                                    })}
                                </div>
                            )}
                        </div>

                        <div className="flex justify-center">
                            <button
                                type="button"
                                onClick={handleSubmit}
                                className="bg-yellow-500 text-white font-bold py-2 px-12 rounded-lg hover:bg-yellow-600 transition"
                            >
                                Submit
                            </button>
                        </div>
                    </div>

                    <div className="bg-white shadow-lg rounded-lg p-8 mt-6">
                        <h2 className="text-xl font-semibold text-center mb-6">
                            Your Daily Attendance Records
                        </h2>
                        {submissions.length > 0 ? (
                            <div className="grid grid-cols-3 gap-4">
                                {submissions.map((submission) => {
                                    const fileName = submission.file_name?.replace(
                                        "attendance_records/",
                                        ""
                                    );
                                    const truncatedFileName =
                                        fileName.length > 20
                                            ? `${fileName.substring(0, 17)}...`
                                            : fileName;

                                    return (
                                        <div
                                            key={submission.submission_id}
                                            className="border border-gray-300 rounded-lg p-4 relative"
                                        >
                                            <button
                                                className="absolute top-2 right-2 text-red-500 font-bold"
                                                onClick={() => handleDelete(submission.submission_id)}
                                            >
                                                X
                                            </button>
                                            <p className="font-medium">
                                                Status:{" "}
                                                <span
                                                    className={`${submission.status === "Approved"
                                                            ? "text-green-500"
                                                            : submission.status === "Rejected"
                                                                ? "text-red-500"
                                                                : "text-blue-500"
                                                        }`}
                                                >
                                                    {submission.status}
                                                </span>
                                            </p>
                                            {submission.status === "Rejected" &&
                                                submission.rejection_reason && (
                                                    <p className="text-black">
                                                        Rejection Reason:{" "}
                                                        {submission.rejection_reason}
                                                    </p>
                                                )}
                                            <p className="text-gray-500 text-sm">
                                                Uploaded On:{" "}
                                                {new Date(
                                                    submission.date_uploaded
                                                ).toLocaleString()}
                                            </p>
                                            {submission.file_name && (
                                                <p className="text-sm text-gray-700 mt-2">
                                                    File Name:{" "}
                                                    <span className="font-mono">
                                                        {truncatedFileName}
                                                    </span>
                                                </p>
                                            )}
                                        </div>
                                    );
                                })}
                            </div>
                        ) : (
                            <p className="text-gray-500 text-center">
                                You have not uploaded any submissions yet.
                            </p>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProponentsDailyAttRec;
