import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const CreateTemplate = () => {
    const navigate = useNavigate();
    const [fields, setFields] = useState({
        include_attendee_name: false,
        include_gender: false,
        include_college: false,
        include_department: false,
        include_year_section: false,
        include_agency_office: false,
        include_contact_number: false,
    });
    const [expirationDate, setExpirationDate] = useState("");
    const [projectId, setProjectId] = useState("");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleChange = (e) => {
        const { name, checked } = e.target;
        setFields((prevFields) => ({ ...prevFields, [name]: checked }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
        const response = await axios.post(`http://localhost:8000/api/attendance/${projectId}/create/`, {
            ...fields,
            expiration_date: expirationDate,
        });
        navigate(`/submit/${response.data.template.id}`);
        } catch (err) {
        setError("Error creating template");
        } finally {
        setLoading(false);
        }
    };

    return (
        <div>
        <h2 className="text-xl font-semibold mb-4">Create Attendance Template</h2>
        {error && <div className="text-red-500 mb-4">{error}</div>}
        <form onSubmit={handleSubmit} className="space-y-4">
            <input
            type="text"
            name="projectId"
            placeholder="Enter Project ID"
            value={projectId}
            onChange={(e) => setProjectId(e.target.value)}
            className="p-2 w-full border border-gray-300 rounded"
            required
            />
            <input
            type="date"
            value={expirationDate}
            onChange={(e) => setExpirationDate(e.target.value)}
            className="p-2 w-full border border-gray-300 rounded"
            required
            />
            <div className="space-y-2">
            {Object.keys(fields).map((field) => (
                <div key={field} className="flex items-center">
                <input
                    type="checkbox"
                    name={field}
                    checked={fields[field]}
                    onChange={handleChange}
                    className="mr-2"
                />
                <label>{field.replace(/_/g, " ")}</label>
                </div>
            ))}
            </div>
            <button
            type="submit"
            disabled={loading}
            className="bg-blue-500 text-white p-2 rounded"
            >
            {loading ? "Creating..." : "Create Template"}
            </button>
        </form>
        </div>
    );
};

export default CreateTemplate;
