import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const FillAttendance = () => {
    const { token } = useParams();
    const [template, setTemplate] = useState(null);
    const [attendanceData, setAttendanceData] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchTemplate = async () => {
        try {
            const response = await axios.get(`http://localhost:8000/api/attendance/fill/${token}/`);
            setTemplate(response.data);
            setLoading(false);
        } catch (err) {
            setError("Error fetching template");
            setLoading(false);
        }
        };
        fetchTemplate();
    }, [token]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAttendanceData({ ...attendanceData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
        await axios.post(`http://localhost:8000/api/attendance/fill/${token}/`, attendanceData);
        alert("Attendance submitted successfully!");
        } catch (err) {
        setError("Error submitting attendance");
        }
    };

    if (loading) return <div>Loading...</div>;

    return (
        <div>
        <h2 className="text-xl font-semibold mb-4">Fill Attendance for Event</h2>
        {error && <div className="text-red-500 mb-4">{error}</div>}
        <form onSubmit={handleSubmit} className="space-y-4">
            {template.fields.map((field) => (
            <div key={field} className="space-y-2">
                <label>{field.replace(/_/g, " ")}</label>
                <input
                type="text"
                name={field}
                value={attendanceData[field] || ""}
                onChange={handleChange}
                className="p-2 w-full border border-gray-300 rounded"
                />
            </div>
            ))}
            <button
            type="submit"
            disabled={loading}
            className="bg-blue-500 text-white p-2 rounded"
            >
            {loading ? "Submitting..." : "Submit Attendance"}
            </button>
        </form>
        </div>
    );
};

export default FillAttendance;
